import React from 'react'
import Dropdown from './Dropdown'
import {useState} from 'react'
import {useEffect} from 'react'

const units = ['Volume', 'Length', 'Weight']
const volume = ['Gallon', 'Liter', 'Quart', 'Pint', 'Cup', 'Milliliter', 'Fluid Ounce']
const volumeConver = {'Gallon':1, 'Liter':3.78541, 'Quart':4, 'Pint':8, 'Cup':16, 'Milliliter':3785.41, 'Fluid Ounce':128}
const length = ['Miles', 'Kilometers', 'Yards', 'Meters', 'Feet', 'Inches', 'Centimeters']
const lengthConver = {'Miles':1, 'Kilometers':1.60934, 'Yards':1760, 'Meters':1609.34, 'Feet':5280, 'Inches':63360, 'Centimeters':160934}
const weight = ['Pounds', 'Kilograms', 'Milligrams', 'Ounces', 'Tons']
const weightConver = {'Pounds':1, 'Kilograms':0.453592, 'Milligrams': 453592, 'Ounces':16, 'Tons':0.0005}

const MainBox = () => {

    const [currentUnit, setCurrentUnit] = useState(units[0])
    const [currentList, setCurrentList] = useState(volume)
    const [currentTo, setCurrentTo] = useState(volume[0])
    const [currentFrom, setCurrentFrom] = useState(volume[0])
    const [init, setInit] = useState()
    const [result, setResult] = useState()

    const onUnitChange = (e) => {
        setCurrentUnit(e.target.value)   
    }

    //Unit change effect
    useEffect(() => {
        console.log(currentUnit)

        if(currentUnit === 'Volume'){
            setCurrentList(volume)
            setCurrentFrom(volume[0])
            setCurrentTo(volume[0])
        }
        else if(currentUnit === 'Length'){
            setCurrentList(length)
            setCurrentFrom(length[0])
            setCurrentTo(length[0])
        }
        else if(currentUnit === 'Weight'){
            setCurrentList(weight)
            setCurrentFrom(weight[0])
            setCurrentTo(weight[0])
        }

    }, [currentUnit])

    const onFromChange = (e) => {
        setCurrentFrom(e.target.value)
    }

    const onToChange = (e) => {
        setCurrentTo(e.target.value)
    }

    useEffect(()=>{
        console.log(currentFrom)
        console.log(currentTo)
        console.log(volumeConver[currentTo])
    }, [currentFrom, currentTo])

    const onInitChange = (e) => {
        setInit(e.target.value)
    }

    useEffect(()=>{
        console.log(init)
    }, [init])

    const onSubmit = () => {

        console.log(currentUnit)

        if(currentUnit === 'Volume'){
            setResult((init/volumeConver[currentFrom]) * volumeConver[currentTo])
        }
        else if(currentUnit === 'Length'){
            setResult((init/lengthConver[currentFrom]) * lengthConver[currentTo])
        }
        else if(currentUnit === 'Weight'){
            setResult((init/weightConver[currentFrom]) * weightConver[currentTo])
        }

    }

    useEffect(() => {
        console.log(result)

    }, [result])

    return(
        <div className='mainBox'>
            <Dropdown id='unit' list={units} onChange = {onUnitChange}></Dropdown>
            <div className='sent'>
                <input type="number" onChange={onInitChange}></input>
                <Dropdown selected={currentFrom} list={currentList} onChange={onFromChange}></Dropdown>
                <h1>=</h1>
                <input type="number"></input>
                <Dropdown selected={currentTo} list = {currentList} onChange={onToChange}></Dropdown>

            </div>
            <button id='submit' onClick={onSubmit}>Convert</button>
        </div>
    )
}

export default MainBox;
