import React from 'react'
import Dropdown from './Dropdown'
import {useState} from 'react'
import {useEffect} from 'react'

const units = ['Volume', 'Length', 'Temperature', 'Weight']
const volume = ['Gallon', 'Liter', 'Quart', 'Pint', 'Cup', 'Milliliter', 'Fluid Ounce']
const length = ['Miles', 'Kilometers', 'Yards', 'Meters', 'Feet', 'Inches', 'Centimeters', 'Millimeters']
const weight = ['Stone', 'Pounds', 'Kilograms', 'Milligrams', 'Grams', 'Ounces']
const temperature = ['Fahrenheit', 'Celsius', 'Kelvin']

const MainBox = () => {

    const [currentUnit, setCurrentUnit] = useState()
    const [currentList, setCurrentList] = useState(volume)

    const onUnitChange = (e) => {
        setCurrentUnit(e.target.value)   
    }

    //Unit change effect
    useEffect(() => {
        console.log(currentUnit)

        switch(currentUnit){
            case 'Volume':
                return setCurrentList(volume)
            case 'Length':
                return setCurrentList(length)
            case 'Temperature':
                return setCurrentList(temperature)
            case 'Weight':
                return setCurrentList(weight)
        }

    }, [currentUnit])



    return(
        <div className='mainBox'>
            <Dropdown id='unit' list={units} onChange = {onUnitChange}></Dropdown>
            <Dropdown id='to' list = {currentTo}></Dropdown>
            <Dropdown id='from' list={currentFrom}></Dropdown>
        </div>
    )
}

export default MainBox;
